  MODIFY `mpreg` DECIMAL(30, 15) DEFAULT NULL,
  MODIFY `patk` DECIMAL(12, 5) DEFAULT NULL,
  MODIFY `pdef` DECIMAL(12, 5) DEFAULT NULL,
